namespace NzKvoDaQm.Tests.Services.RecipeImagesService
{

    using System;
    using System.Data.Entity;
    using System.IO;
    using System.Linq;

    using Microsoft.VisualStudio.TestTools.UnitTesting;

    using NzKvoDaQm.Data;
    using NzKvoDaQm.Models.EntityModels;
    using NzKvoDaQm.Services.Interfaces;
    using NzKvoDaQm.Services.Recipe;

    [TestClass]
    public class CreateTests
    {
        private const string Image64Base = "data:image/png;base64,";

        private IRecipeImagesService service;
        private IDbSet<RecipeImage> set;
        private IDbContext context;

        [TestInitialize]
        public void Initialize()
        {
            var dataGenerator = new TestDataGenerator();
            this.context = dataGenerator.GenerateContext();
            this.set = this.context.RecipeImages;
            this.service = new RecipeImagesService(this.set, this.context);
        }

        [TestMethod]
        [ExpectedException(typeof(ArgumentNullException))]
        public void CantCreateIfEmptyString()
        {
            var author = this.context.Users.First();
            this.service.Create(null, author);
        }

        [TestMethod]
        [ExpectedException(typeof(ArgumentNullException))]
        public void CantCreateIfImageIsWhiteSpaces()
        {
            var author = this.context.Users.First();
            this.service.Create("                    ", author);
        }

        [TestMethod]
        [ExpectedException(typeof(ArgumentException))]
        public void CantCreateIfInvalidImage()
        {
            var author = this.context.Users.First();
            this.service.Create("imageimageimage", author);
        }

        [TestMethod]
        [ExpectedException(typeof(ArgumentException))]
        public void CantCreateIfImageTooBig()
        {
            var imageData = "data:image/png;base64," + new string('A', RecipeImagesService.MaxImageSize + 1);
            var author = this.context.Users.First();
            this.service.Create(imageData, author);
        }

        [TestMethod]
        [ExpectedException(typeof(ArgumentNullException))]
        public void CantCreateIfAuthorIsNull()
        {
            this.service.Create(Image64Base, null);
        }

        [TestMethod]
        public void Created()
        {
            var author = this.context.Users.First();
            var image = this.service.Create(Image64Base, author);
            Assert.AreEqual(Image64Base, image.Url);
            Assert.AreEqual(author, image.Author);
        }
    }
}